sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({api, event, helpers, imports}) {

    const noteID = api.state.noteSysId;
    const title = api.state.activeNoteTitle || api.data.look_up_note.result.title.value;
    const body = api.state.activeNoteBody || api.data.look_up_note.result.note.value;   
    const updateQuery = `title=${title}^note=${body}`;

    api.data.update_note.execute({
        "table": 'x_snc_createnotes_note',
        "recordId": noteID,
        "templateFields": updateQuery,
        "useSetDisplayValue": false
    });

}
